import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card(props) {

    const userData = React.useContext(CurrentUserContext);

    // Определяем, являемся ли мы владельцем текущей карточки
    const isOwn = props.owner._id === userData._id;

    // Создаём переменную, которую после зададим в `className` для кнопки удаления
    const cardDeleteButtonClassName = (
        `card__delete ${isOwn ? 'card__delete' : ''}`
    );

    // Определяем, есть ли у карточки лайк, поставленный текущим пользователем
    const isLiked = props.likes.some(i => i._id === userData._id);

    // Создаём переменную, которую после зададим в `className` для кнопки лайка
    const cardLikeButtonClassName = `card__like ${isLiked ? 'card__like_active' : ''}`;

    function handleClick() {
        props.onCardClick(props);
    }

    return (
        <div key={props.id} className="card">
            <div className="card">
                <button type="button" className={`${cardDeleteButtonClassName} hover`}></button>
                <img src={props.link} className="card__img" alt={props.name} onClick={handleClick} />
                <div className="card__description">
                    <h2 className="card__text">{props.name}</h2>
                    <div className="">
                        <button type="button" className={cardLikeButtonClassName} onClick={props.onCardLike} aria-label="Нравится"></button>
                        <p className="card__like_number"></p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Card;
