import React from 'react';

function Login(props) {

    const [emailValue, setEmailValue] = React.useState('');
    const [passwordValue, setPasswordValue] = React.useState('');

    function handleChangeEmail(e) {
        setEmail(e.target.value);
    }

    function handleChangePassword(e) {
        setPassword(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();

        const email = emailValue;
        const password = passwordValue;

        props.authorization(email, password);
    }

    return (
    <div className="login">
        <h1 className="login__title">Вход</h1>
        <form className="login__form" onSubmit={handleSubmit}>
            <input className="login__input" placeholder="Email" value={email} onChange={handleChangeEmail}></input>
            <input className="login__input" placeholder="Пароль" value={password} onChange={handleChangePassword}></input>
            <button className="login__button" type="submit">Войти</button>
        </form>
    </div>
    )
}

export default Login;
