    return (
        <PopupWithForm
            name='avatar_update'
            title='Обновить аватар'
            buttonText='Создать'

            isOpen={props.isOpen}
            onClose={props.onClose}
            onSubmit={handleSubmit}
        >

            <input id="avatarurl" type="url" name="link"
                className={`form-container__input form-container__input_${props.name}`}
                placeholder="Ссылка на картинку" tabIndex="2" required onChange={handleChangeAvatar}
                value={avatar || ''} />
            <span className="form-container__input-error" id="avatarurl-error"></span>
        </PopupWithForm>
    )
}

export default EditAvatarPopup;
