import React from 'react';

function Register({ registration }) {

    const [emailValue, setEmailValue] = React.useState('');
    const [passwordValue, setPasswordValue] = React.useState('');

    function handleChangeEmail(e) {
        setEmailValue(e.target.value);
    }

    function handleChangePassword(e) {
        setPasswordValue(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();

        const email = emailValue;
        const password = passwordValue;

        registration(email, password);
    }

    return (
        <div className="register">
            <h1 className="register__title">Регистрация</h1>
            <form className="register__form" onSubmit={handleSubmit}>
                <input className="register__input" placeholder="Email" value={emailValue} onChange={handleChangeEmail}></input>
                <input className="register__input" placeholder="Пароль" value={passwordValue} onChange={handleChangePassword}></input>
                <button className="register__button" type="submit">Зарегистрироваться</button>
            </form>
        </div>
    )
}

export default Register ;
